INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1, 'wc-admin-welcome-note', 'info', 'en_US', 'New feature(s)', 'Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-28 01:20:29', NULL, 0),
(2, 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Looking for the Store Notice setting?', 'It can now be found in the Customizer.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-28 01:20:31', NULL, 0),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-28 01:20:41', NULL, 0),
(4, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Welcome to WooCommerce! Set up your store and start selling', 'We\'re here to help you going through the most important steps to get your store up and running.', 'info', '{}', 'actioned', 'woocommerce-admin', '2020-05-28 01:20:55', NULL, 0),
(5, 'wc-admin-mobile-app', 'info', 'en_US', 'Instala la aplicación móvil WooCommerce', 'Instala la aplicación móvil de WooCommerce para gestionar pedidos, recibir avisos de ventas y ver métricas clave – allí donde estés.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-05-30 06:12:48', NULL, 0),
(6, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Trucos, actualizaciones de productos e inspiración', 'Estamos aquí por ti - consigue trucos, actualizaciones de productos e inspiración en tu buzón.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-05-30 06:12:49', NULL, 0),
(7, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Valora tu experiencia', '´Si te gusta WooCommerce Admin, por favor, déjanos una valoración de 5 estrellas. ¡Muchas gracias por adelantado!', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-31 18:03:05', NULL, 0),
(8, 'wc-admin-facebook-extension', 'info', 'en_US', 'Vende en Facebook', 'Haz crecer tu negocio dirigiéndote a las personas adecuadas e impulsando las ventas con Facebook. Puedes instalar ahora esta extensión gratuita.', 'thumbs-up', '{}', 'actioned', 'woocommerce-admin', '2020-05-31 18:03:13', NULL, 0);
